import React, { Component } from 'react';
import './Cat.css'
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';

class Cat extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      currentPage: 1,
      gifsPerPage: 10,
      modal: false,
      imageKey
    };
    this.handleClick = this.handleClick.bind(this);
    this.toggle = this.toggle.bind(this);
    this.imageId = this.imageId.bind(this);
  }

  handleClick(event) {
    this.setState({
      currentPage: Number(event.target.id)
    });
  }

  toggle() {
    this.setState(prevState => ({
      modal: !prevState.modal,
    }));
  }

  imageId(event){
      this.setState({
          imageKey: Number(event.target.id)
      })

  }

  componentDidMount() {
    fetch("http://api.giphy.com/v1/gifs/search?q=cat&api_key=5d70ONYFLMXtwvOMuP6bwJ05CSSzHxYC&limit=30")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            data: result.data
          });
        }
      )
  }

  render() {
    const { data, currentPage, gifsPerPage, key } = this.state;

    const indexOfLastGif = currentPage * gifsPerPage;
    const indexOfFirstGif = indexOfLastGif - gifsPerPage;
    const currentGifs = data.slice(indexOfFirstGif, indexOfLastGif);

    const renderGifs = currentGifs.map(data => (
            <img key={data.id} className="eachGif" src = {data.images.downsized.url} alt={data.title} onClick={(event) => { this.toggle(); this.imageId();}}/>
      ));

      const pageNumbers = [];
        for (let i = 1; i <= Math.ceil(data.length / gifsPerPage); i++) {
          pageNumbers.push(i);
        }

        const renderPageNumbers = pageNumbers.map(number => {
          return (
            <button
              className = "pageNumbers"
              key={number}
              id={number}
              onClick={this.handleClick}
            >
              {number}
            </button>
          );
        });
      return (
        <div className="CatContainer">
            <div className="pageNumbersDiv">
                {renderPageNumbers}
            </div>
            <div className = "gifsDiv">
                {renderGifs}
                {}
                <Modal isOpen={this.state.modal} toggle={this.toggle}>
                    <ModalHeader toggle={this.toggle}>{renderGifs[key]}</ModalHeader>
                    <ModalBody>

                    </ModalBody>
                    <ModalFooter>
                    <Button color="secondary" onClick={this.toggle}>OK</Button>
                    </ModalFooter>
                </Modal>
            </div>
        </div>
      );
  }
}

export default Cat;
